import Header from "../../components/Header";
import background from '../../assets/background.svg';
import ItemList from "../../components/ItemList";
import "./styles.css"

import { useState } from "react";

function App() {
  const [user, setUser] = useState('');
const [currentUser, setCurrentUser] = useState(null);
const [repos, setRepos] = useState(null);

// Método de Busca [assíncrona]
const handleSetData = async () => {
  //1-Busca de usuario
  const userData = await fetch(`https://api.github.com/users/${user}`);
  const newUser = await userData.json();
  //console.log(newUser);
  if(newUser.name){
    const {avatar_url, name, bio} = newUser;
    setCurrentUser({avatar_url, name, bio})

    //2-Busca dos Repositorios
    const reposData = await fetch(`https://api.github.com/users/${user}/repos`);
    const newRepos = await userData.json();

    if(newRepos.length){
      setRepos(newRepos);
    }
  }
};




  return (
    <div className="App">
      <Header />
      <div className="conteudo">
        <img src={background} className="background" alt="background app" />
        {/* Div do Algoritmo de Busca por usuario */}
        <div className="info">
          <div>
            <input
              type="text" value={user} name="usuario"
              onChange={event => setUser(event.target.value)}
              placeholder="@username" />
            <button onClick={handleSetData}>Buscas</button>
          </div>
        </div>
        <br></br>
        {currentUser?.name ? (<>
          <div className="perfil">
          <img src="https://avatars.githubusercontent.com/u/21243659?v=4" className="profile" alt="imagem de perfil" />
          <div>

            <h3>Jorge Luis Sampaio de Oliveira</h3>
            <span>@jorgeluis-dev</span>
            <p>Descrição: Software Engineer | Java Back-end |</p>
          </div>
        </div>
        <hr />

</>): null}


{repos?.lenght ? (<>

        <div>
          <h4 className="repositorio">Repositorios</h4>
          <ItemList
            title="teste1"
            description="teste"
          />
          <ItemList
            title="teste2"
            description="teste"
          />
          <ItemList
            title="teste3"
            description="teste"
          />
        </div>
</>): null}
      </div>

    </div>
  );
}

export default App;
